package com.example.recyclerjava;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.content.Intent;
import android.os.Bundle;
import android.widget.Toast;

import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity implements RecyclerPersonas.OnSelected {

    RecyclerView recyclerView;

    RecyclerPersonas recyclerPersonas;

    List<Persona> listaPersonas;



    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        instancias();

        recyclerView.setAdapter(recyclerPersonas);
        recyclerView.setLayoutManager(new LinearLayoutManager(getApplicationContext(),RecyclerView.VERTICAL,false));
        recyclerPersonas.notifyDataSetChanged();


        listaPersonas.add( new Persona("data:image/png;base64,","Angular","framework"));
        listaPersonas.add( new Persona("https://www.anerbarrena.com/wp-content/uploads/2016/05/logo-programacion-1280x720.jpg","PHP","Lenguage"));
        listaPersonas.add( new Persona("data:image/png;base64,","HTML","Maquetado"));
        listaPersonas.add( new Persona("https://upload.wikimedia.org/wikipedia/commons/thumb/9/99/Unofficial_JavaScript_logo_2.svg/1200px-Unofficial_JavaScript_logo_2.svg.png","JavaScript","Lenguage"));
        listaPersonas.add( new Persona("https://d1.awsstatic.com/asset-repository/products/amazon-rds/1024px-MySQL.ff87215b43fd7292af172e2a5d9b844217262571.png","Mysql","Lenguage"));

    }

    private void instancias() {

        listaPersonas = new ArrayList();
        recyclerView = findViewById(R.id.listado);
        recyclerPersonas = new RecyclerPersonas(listaPersonas,MainActivity.this);


    }


    @Override
    public void SelectedNombre(Persona persona) {
        Toast.makeText(getApplicationContext(),"Nombre: "+persona.nombre,Toast.LENGTH_SHORT).show();
        Intent intent = new Intent(this,Detalle.class);
        intent.putExtra("imagen",persona.imagen);
        intent.putExtra("nombre",persona.nombre);
        intent.putExtra("apellido",persona.apellido);
        startActivity(intent);
    }
}